  --secondary: hsl(217 33% 17%);
  --secondary-foreground: hsl(210 40% 98%);
  --muted: hsl(217 33% 17%);
  --muted-foreground: hsl(215 20% 65%);
  --accent: hsl(217 33% 17%);
  --accent-foreground: hsl(208 88% 52%);
  --destructive: hsl(0 62% 30%);
  --destructive-foreground: hsl(0 85% 97%);
