  --chart-1: hsl(208 88% 52%);
  --chart-2: hsl(155 100% 36%);
  --chart-3: hsl(30 95% 64%);
  --chart-4: hsl(147 62% 44%);
  --chart-5: hsl(341 75% 61%);
  --sidebar: hsl(222 84% 5%);
  --sidebar-foreground: hsl(210 40% 98%);
  --sidebar-primary: hsl(208 88% 52%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(217 33% 17%);
  --sidebar-accent-foreground: hsl(208 88% 52%);
  --sidebar-border: hsl(217 33% 17%);
  --sidebar-ring: hsl(208 88% 52%);

  /* Hospital-specific dark colors */
  --medical-blue: hsl(208 88% 52%);
  --healthcare-green: hsl(155 62% 44%);
  --alert-orange: hsl(14 95% 70%);
  --light-gray: hsl(217 33% 12%);
  --surface: hsl(222 84% 5%);
  --text-dark: hsl(210 40% 98%);
  --text-muted: hsl(215 20% 65%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-family: var(--font-sans);
  }
}

@layer utilities {
  .medical-blue {
    color: var(--medical-blue);
  }

  .bg-medical-blue {
    background-color: var(--medical-blue);
  }

  .healthcare-green {
    color: var(--healthcare-green);
  }

  .bg-healthcare-green {
    background-color: var(--healthcare-green);
  }

  .alert-orange {
    color: var(--alert-orange);
  }

  .bg-alert-orange {
    background-color: var(--alert-orange);
  }

  .text-medical-blue {
    color: var(--medical-blue);
  }

  .text-healthcare-green {
    color: var(--healthcare-green);
  }

  .text-alert-orange {
    color: var(--alert-orange);
  }
}

