                  onClick={() => {
                    const userRoles = user?.roles || [user?.role];
                    const isBillingStaff = userRoles.includes('billing_staff') && !userRoles.includes('admin') && !userRoles.includes('super_user');

                    if (isBillingStaff) {
                      setIsAccessDeniedPatientOpen(true);
                    } else {
                      setIsNewPatientOpen(true);
                    }
                  }}
                  className="p-4 bg-healthcare-green text-white rounded-lg hover:bg-healthcare-green/90 transition-colors" 
                  data-testid="quick-new-patient"
                >
                  <div className="text-center">
                    <div className="text-lg font-semibold">Add Patient</div>
                    <div className="text-sm opacity-90">Register new</div>
                  </div>
                </button>

                <button 
                  onClick={() => {
                    const userRoles = user?.roles || [user?.role];
                    const isBillingStaff = userRoles.includes('billing_staff') && !userRoles.includes('admin') && !userRoles.includes('super_user');

                    if (isBillingStaff) {
                      setIsAccessDeniedLabTestOpen(true);
                    } else {
                      setIsPathologyOrderOpen(true);
                    }
                  }}
                  className="p-4 bg-purple-500 text-white rounded-lg hover:bg-purple-500/90 transition-colors" 
                  data-testid="quick-new-test"
                >
                  <div className="text-center">
                    <div className="text-lg font-semibold">Lab Test</div>
                    <div className="text-sm opacity-90">Order test</div>
                  </div>
                </button>

                <button 
                  onClick={() => navigate("/pending-bills")}
                  className="p-4 bg-alert-orange text-white rounded-lg hover:bg-alert-orange/90 transition-colors" 
                  data-testid="quick-view-pending"
                >
                  <div className="text-center">
                    <div className="text-lg font-semibold">Pending</div>
                    <div className="text-sm opacity-90">View bills</div>
                  </div>
                </button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Fake Bill Dialog */}
      <FakeBillDialog 
        isOpen={isFakeBillDialogOpen}
        onClose={() => setIsFakeBillDialogOpen(false)}
      />

      {/* New Patient Dialog */}
      <Dialog open={isNewPatientOpen} onOpenChange={setIsNewPatientOpen}>
        <DialogContent className="max-w-2xl" data-testid="new-patient-dialog">
          <DialogHeader>
            <DialogTitle>Register New Patient</DialogTitle>
          </DialogHeader>

          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="name">Full Name *</Label>
                <Input
                  id="name"
                  {...form.register("name")}
                  placeholder="Enter patient's full name"
                  data-testid="input-patient-name"
                />
                {form.formState.errors.name && (
                  <p className="text-sm text-destructive">{form.formState.errors.name.message}</p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="age">Age *</Label>
                <Input
                  id="age"
                  type="number"
                  {...form.register("age", { valueAsNumber: true })}
                  placeholder="Enter age"
                  data-testid="input-patient-age"
                />
                {form.formState.errors.age && (
                  <p className="text-sm text-destructive">{form.formState.errors.age.message}</p>
                )}
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="gender">Gender *</Label>
                <Select 
                  value={form.watch("gender")}
                  onValueChange={(value) => form.setValue("gender", value, { shouldValidate: true })}
                >
                  <SelectTrigger data-testid="select-patient-gender">
                    <SelectValue placeholder="Select gender" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="male">Male</SelectItem>
                    <SelectItem value="female">Female</SelectItem>
                    <SelectItem value="other">Other</SelectItem>
                  </SelectContent>
                </Select>
                {form.formState.errors.gender && (
                  <p className="text-sm text-destructive">{form.formState.errors.gender.message}</p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="phone">Phone Number *</Label>
                <Input
                  id="phone"
                  {...form.register("phone")}
                  placeholder="+91 XXXXX XXXXX"
                  data-testid="input-patient-phone"
                />
                {form.formState.errors.phone && (
                  <p className="text-sm text-destructive">{form.formState.errors.phone.message}</p>
                )}
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="email">Email Address</Label>
              <Input
                id="email"
                type="email"
                {...form.register("email")}
                placeholder="patient@example.com"
                data-testid="input-patient-email"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="address">Address</Label>
              <Textarea
                id="address"
                {...form.register("address")}
                placeholder="Enter complete address"
                rows={3}
                data-testid="input-patient-address"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="emergencyContact">Emergency Contact</Label>
              <Input
                id="emergencyContact"
                {...form.register("emergencyContact")}
                placeholder="+91 XXXXX XXXXX"
                data-testid="input-patient-emergency"
              />
            </div>

            <div className="flex justify-end space-x-2 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsNewPatientOpen(false)}
                data-testid="button-cancel-patient"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createPatientMutation.isPending || !form.formState.isValid}
                className="bg-medical-blue hover:bg-medical-blue/90"
                data-testid="button-save-patient"
              >
                {createPatientMutation.isPending ? "Saving..." : "Register Patient"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {/* Order Pathology Tests Dialog */}
      <Dialog open={isPathologyOrderOpen} onOpenChange={setIsPathologyOrderOpen}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Order Pathology Tests</DialogTitle>
          </DialogHeader>

          <form onSubmit={pathologyForm.handleSubmit(onPathologySubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="patientId">Patient *</Label>
                <PatientSearchCombobox
                  value={pathologyForm.watch("patientId")}
                  onValueChange={(value) => pathologyForm.setValue("patientId", value)}
                  patients={patients || []}
                />
                {pathologyForm.formState.errors.patientId && (
                  <p className="text-sm text-red-500">{pathologyForm.formState.errors.patientId.message}</p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="doctorId">Doctor (Optional for External Patients)</Label>
                <Select 
                  onValueChange={(value) => pathologyForm.setValue("doctorId", value)}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select doctor (optional)" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="external">External Patient (No Doctor)</SelectItem>
                    {(doctors || []).map((doctor: any) => (
                      <SelectItem key={doctor.id} value={doctor.id}>
                        {doctor.name} - {doctor.specialization}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <Label>Select Tests from Catalog</Label>
                <div className="flex items-center space-x-2">
                  <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                    <SelectTrigger className="w-48">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Categories</SelectItem>
                      {(categories || []).map((category: string) => (
                        <SelectItem key={category} value={category}>{category}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <div className="flex-1">
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                    <Input
                      placeholder="Search tests by name..."
                      value={catalogSearchQuery}
                      onChange={(e) => setCatalogSearchQuery(e.target.value)}
                      className="pl-10"
                    />
                  </div>
                </div>
              </div>

              <div className="border rounded-lg max-h-64 overflow-y-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead className="w-12">Select</TableHead>
                      <TableHead>Test Name</TableHead>
                      <TableHead>Category</TableHead>
                      <TableHead>Price ()</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {filteredCatalog.map((test: any, index: number) => {
                      const isSelected = selectedCatalogTests.some(t => t.test_name === test.test_name);
                      return (
                        <TableRow 
                          key={`${test.category}-${test.test_name}-${index}`}
                          className={isSelected ? "bg-blue-50" : ""}
                        >
                          <TableCell>
                            <input
                              type="checkbox"
                              checked={isSelected}
                              onChange={() => toggleTestSelection(test)}
                              className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                            />
                          </TableCell>
                          <TableCell className="font-medium">{test.test_name}</TableCell>
                          <TableCell>{test.category}</TableCell>
                          <TableCell>{test.price}</TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </div>

              {selectedCatalogTests.length > 0 && (
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="font-medium text-blue-900 mb-2">Selected Tests ({selectedCatalogTests.length})</h4>
                  <div className="space-y-1">
                    {selectedCatalogTests.map((test, index) => (
                      <div key={index} className="flex justify-between text-sm">
                        <span>{test.test_name}</span>
                        <span>{test.price}</span>
                      </div>
                    ))}
                  </div>
                  <div className="border-t border-blue-200 mt-2 pt-2 font-medium text-blue-900">
                    Total: {getTotalPrice()}
                  </div>
                </div>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="remarks">Remarks</Label>
              <Textarea
                {...pathologyForm.register("remarks")}
                placeholder="Enter any additional remarks or instructions"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsPathologyOrderOpen(false)}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createOrderMutation.isPending || selectedCatalogTests.length === 0}
              >
                {createOrderMutation.isPending ? "Ordering..." : `Order ${selectedCatalogTests.length} Test(s)`}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {/* View Order Details Dialog */}
      {selectedOrder && (
        <OrderDetailsDialog 
          order={selectedOrder} 
          onClose={() => {
            setSelectedOrder(null);
            // No redirect needed since we're already on dashboard
          }} 
        />
      )}

      {/* Access Denied Dialogs for Billing Staff */}
      <Dialog open={isAccessDeniedPatientOpen} onOpenChange={setIsAccessDeniedPatientOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Access Restricted</DialogTitle>
          </DialogHeader>
          <div className="p-4">
            <AccessRestricted 
              title="Patient Registration Restricted"
              description="Only administrators and super users can register new patients."
            />
          </div>
        </DialogContent>
      </Dialog>

      <Dialog open={isAccessDeniedLabTestOpen} onOpenChange={setIsAccessDeniedLabTestOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Access Restricted</DialogTitle>
          </DialogHeader>
          <div className="p-4">
            <AccessRestricted 
              title="Lab Test Ordering Restricted"
              description="Only administrators and super users can order lab tests."
            />
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/services.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import TopBar from "@/components/layout/topbar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Building2,
  Plus,
  Edit,
  Trash2,
  Bed,
  Home,
  Activity,
  AlertTriangle,
  Heart,
  Stethoscope,
  Syringe,
  Scissors,
  Settings,
  Shield
} from "lucide-react";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import type { RoomType, Room, Service, PathologyCategory, DynamicPathologyTest, ServiceCategory } from "@shared/schema";
import AccessRestricted from "@/components/access-restricted";

export default function ServiceManagement() {
  const { toast } = useToast();
  const { user } = useAuth();

  // Tab management state
  const [activeTab, setActiveTab] = useState("rooms");

  const userRoles = user?.roles || [user?.role]; // Backward compatibility
  const hasAccess = userRoles.includes('admin') || userRoles.includes('super_user');

  if (!hasAccess) {
    return (
      <div className="space-y-6">
        <TopBar title="Service Management" />
        <div className="p-6">
          <AccessRestricted 
            title="Access Restricted"
            description="Only administrators and super users can access service management."
          />
        </div>
      </div>
    );
  }
  const [roomsSubTab, setRoomsSubTab] = useState("room-types");
  const [selectedRoomTypeId, setSelectedRoomTypeId] = useState<string>("");
  const [isRoomTypeDialogOpen, setIsRoomTypeDialogOpen] = useState(false);
  const [isRoomDialogOpen, setIsRoomDialogOpen] = useState(false);
  const [isServiceDialogOpen, setIsServiceDialogOpen] = useState(false);
  const [editingRoomType, setEditingRoomType] = useState<RoomType | null>(null);
  const [editingRoom, setEditingRoom] = useState<Room | null>(null);
  const [editingService, setEditingService] = useState<Service | null>(null);
  const [serviceDoctors, setServiceDoctors] = useState<{id: string, share: number}[]>([]);

  // Pathology states
  const [pathologySubTab, setPathologySubTab] = useState("categories");
  const [selectedCategoryId, setSelectedCategoryId] = useState<string>("");
  const [isCategoryDialogOpen, setIsCategoryDialogOpen] = useState(false);
  const [isTestDialogOpen, setIsTestDialogOpen] = useState(false);
  const [isUploadDialogOpen, setIsUploadDialogOpen] = useState(false);
  const [editingCategory, setEditingCategory] = useState<PathologyCategory | null>(null);
  const [editingTest, setEditingTest] = useState<DynamicPathologyTest | null>(null);
  const [uploadData, setUploadData] = useState<string>("");

  // Service Category states
  const [isServiceCategoryDialogOpen, setIsServiceCategoryDialogOpen] = useState(false);
  const [editingServiceCategory, setEditingServiceCategory] = useState<ServiceCategory | null>(null);

  const token = localStorage.getItem("hospital_token");

  // Fetch room types
  const { data: roomTypes = [], refetch: refetchRoomTypes } = useQuery<RoomType[]>({
    queryKey: ["/api/room-types"],
  });

  // Fetch rooms
  const { data: rooms = [], refetch: refetchRooms } = useQuery<Room[]>({
    queryKey: ["/api/rooms"],
  });

  // Fetch services
  const { data: services = [], refetch: refetchServices } = useQuery<Service[]>({
    queryKey: ["/api/services"],
  });

  // Fetch doctors for service assignment and display
  const { data: doctors = [] } = useQuery<any[]>({
    queryKey: ["/api/doctors"],
  });

  // Fetch patient services to display in history
  const { data: patientServices = [] } = useQuery<any[]>({
    queryKey: ["/api/patient-services"],
  });

  // Fetch pathology categories
  const { data: pathologyCategories = [], refetch: refetchCategories } = useQuery<PathologyCategory[]>({
    queryKey: ["/api/pathology-categories"],
  });

  // Fetch dynamic pathology tests
  const { data: dynamicPathologyTests = [], refetch: refetchTests } = useQuery<DynamicPathologyTest[]>({
    queryKey: ["/api/dynamic-pathology-tests"],
  });

  // Fetch combined pathology data (hardcoded + dynamic)
  const { data: combinedPathologyData, isLoading: combinedLoading, refetch: refetchCombined } = useQuery({
    queryKey: ["/api/pathology-tests/combined"],
    staleTime: 5 * 60 * 1000, // Cache for 5 minutes
  });

  // Fetch service categories
  const { data: customServiceCategories = [], refetch: refetchServiceCategories } = useQuery<ServiceCategory[]>({
    queryKey: ["/api/service-categories"],
  });

  const roomTypeForm = useForm({
    defaultValues: {
      name: "",
      category: "",
      dailyCost: 0,
      isActive: true,
    },
  });

  const roomForm = useForm({
    defaultValues: {
      roomNumber: "",
      roomTypeId: "",
      floor: "",
      building: "",
      capacity: 1,
      isOccupied: false,
      isActive: true,
      notes: "",
    },
  });

  const serviceForm = useForm({
    defaultValues: {
      name: "",
      category: "",
      price: 0,
      description: "",
      isActive: true,
      doctors: [],
      billingType: "per_instance", // Default billing type
      billingParameters: null, // For composite billing
    },
  });

  const categoryForm = useForm({
    defaultValues: {
      name: "",
      description: "",
      isActive: true,
    },
  });

  const testForm = useForm({
    defaultValues: {
      categoryId: "",
      testName: "",
      price: 0,
      normalRange: "",
      description: "",
      isActive: true,
    },
  });

  const serviceCategoryForm = useForm({
    defaultValues: {
      name: "",
      label: "",
      description: "",
      icon: "Settings",
      isActive: true,
    },
  });

  const createRoomTypeMutation = useMutation({
    mutationFn: async (data: any) => {
      const isEditing = editingRoomType !== null;
      const url = isEditing ? `/api/room-types/${editingRoomType.id}` : "/api/room-types";
      const method = isEditing ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("hospital_token")}`,
        },
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to ${isEditing ? 'update' : 'create'} room type: ${errorText}`);
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/room-types"] });
      setIsRoomTypeDialogOpen(false);
      roomTypeForm.reset();
      const wasEditing = editingRoomType !== null;
      setEditingRoomType(null);
      toast({
        title: "Success",
        description: `Room type ${wasEditing ? 'updated' : 'created'} successfully`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save room type",
        variant: "destructive",
      });
    },
  });

  const createRoomMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch("/api/rooms", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("hospital_token")}`,
        },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to create room");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/rooms"] });
      setIsRoomDialogOpen(false);
      roomForm.reset();
      setEditingRoom(null);
      toast({
        title: "Success",
        description: "Room saved successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save room",
        variant: "destructive",
      });
    },
  });

  const createServiceMutation = useMutation({
    mutationFn: async (data: any) => {
      const isEditing = editingService !== null;
      const url = isEditing ? `/api/services/${editingService.id}` : "/api/services";
      const method = isEditing ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("hospital_token")}`,
        },
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to ${isEditing ? 'update' : 'create'} service: ${errorText}`);
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setIsServiceDialogOpen(false);
      serviceForm.reset();
      const wasEditing = editingService !== null;
      setEditingService(null);
      setServiceDoctors([]);
      toast({
        title: "Success",
        description: `Service ${wasEditing ? 'updated' : 'created'} successfully`,
      });
    },
    onError: (error: any) => {
      console.error("Service creation/update error:", error);
      let errorMessage = "Failed to save service";
      if (error.message) {
        errorMessage = error.message;
      }
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const deleteServiceMutation = useMutation({
    mutationFn: async (serviceId: string) => {
      const response = await fetch(`/api/services/${serviceId}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("hospital_token")}`,
        },
      });
      if (!response.ok) throw new Error("Failed to delete service");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      toast({
        title: "Success",
        description: "Service deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete service",
        variant: "destructive",
      });
    },
  });

  const deleteRoomTypeMutation = useMutation({
    mutationFn: async (roomTypeId: string) => {
      const response = await fetch(`/api/room-types/${roomTypeId}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("hospital_token")}`,
        },
      });
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to delete room type: ${errorText}`);
      }
      // For 204 responses, there might not be JSON content
      if (response.status === 204) {
        return { success: true };
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/room-types"] });
      queryClient.invalidateQueries({ queryKey: ["/api/rooms"] });
      toast({
        title: "Success",
        description: "Room type deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete room type",
        variant: "destructive",
      });
    },
  });

  const deleteRoomMutation = useMutation({
    mutationFn: async (roomId: string) => {
      const response = await fetch(`/api/rooms/${roomId}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("hospital_token")}`,
        },
      });
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to delete room: ${errorText}`);
      }
      // For 204 responses, there might not be JSON content
      if (response.status === 204) {
        return { success: true };
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/rooms"] });
      queryClient.invalidateQueries({ queryKey: ["/api/room-types"] });
      toast({
        title: "Success",
        description: "Room deleted successfully",
      });
    },
