            .section-title {
              font-size: 16px;
              font-weight: bold;
              margin: 15px 0 10px 0;
              padding: 5px 10px;
              background: #f3f4f6;
              border-left: 4px solid #2563eb;
            }

            .signature-section {
              margin: 35px 0;
              display: flex;
              justify-content: space-between;
              align-items: center;
            }

            .signature-box {
              text-align: center;
              min-width: 200px;
            }

            .signature-line {
              border-bottom: 1px solid #333;
              margin-bottom: 5px;
              height: 40px;
            }

