        serviceName: "",
        scheduledDate: "",
        scheduledTime: "",
        doctorId: "",
        serviceId: "", // Reset serviceId
        notes: "",
        price: 0,
        quantity: 1,
        hours: 1,
        distance: 0,
      });
      toast({
        title: "Service scheduled successfully",
        description: "The service has been added to the patient's schedule.",
      });
    },
    onError: () => {
      toast({
        title: "Error scheduling service",
        description: "Please try again.",
        variant: "destructive",
      });
    },
  });

