      </Dialog>

      {/* Payment Dialog */}
      <Dialog open={isPaymentDialogOpen} onOpenChange={setIsPaymentDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Payment</DialogTitle>
          </DialogHeader>

          <div className="py-4">
            <div className="space-y-4">
              <div className="space-y-2">
                <Label>Payment Amount *</Label>
                <Input
                  type="number"
                  min="0"
                  step="0.01"
                  value={paymentAmount}
                  onChange={(e) => setPaymentAmount(e.target.value)}
                  placeholder="Enter payment amount"
                  data-testid="input-payment-amount"
                />
              </div>

              <div className="space-y-2">
                <Label>Payment Method</Label>
                <Select defaultValue="cash">
                  <SelectTrigger data-testid="select-payment-method">
                    <SelectValue placeholder="Select payment method" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="cash">Cash</SelectItem>
                    <SelectItem value="card">Card</SelectItem>
                    <SelectItem value="upi">UPI</SelectItem>
                    <SelectItem value="bank-transfer">Bank Transfer</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </div>

          <div className="flex justify-end gap-2">
            <Button
                type="button"
                variant="outline"
                onClick={() => setIsPaymentDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button
                onClick={() => {
                  const amount = parseFloat(paymentAmount);
                  if (amount > 0) {
                    addPaymentMutation.mutate({
                      amount: amount,
                      paymentMethod: "cash", // Default to cash, can be extended with a dropdown
                      reason: "Payment",
                    });
                  }
                }}
                disabled={
                  addPaymentMutation.isPending ||
                  !paymentAmount ||
                  parseFloat(paymentAmount) <= 0
                }
                className="bg-green-600 hover:bg-green-700 text-white"
              >
                {addPaymentMutation.isPending
                  ? "Adding Payment..."
                  : "Add Payment"}
              </Button>
            </div>
          </DialogContent>
        </Dialog>

        {/* Discount Dialog */}
        <Dialog
          open={isDiscountDialogOpen}
          onOpenChange={setIsDiscountDialogOpen}
        >
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Add Discount</DialogTitle>
            </DialogHeader>

            <div className="py-4">
              <div className="space-y-4">
                <div className="space-y-2">
                  <Label>Discount Amount *</Label>
                  <Input
                    type="number"
                    min="0"
                    step="0.01"
                    value={discountAmount}
                    onChange={(e) => setDiscountAmount(e.target.value)}
                    placeholder="Enter discount amount"
                    data-testid="input-discount-amount"
                  />
                </div>

                <div className="space-y-2">
                  <Label>Reason for Discount</Label>
                  <Input
                    type="text"
                    value={discountReason}
                    onChange={(e) => setDiscountReason(e.target.value)}
                    placeholder="Enter reason for discount (optional)"
                    data-testid="input-discount-reason"
                  />
                </div>
              </div>
            </div>

            <div className="flex justify-end gap-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsDiscountDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button
                onClick={() => {
                  const amount = parseFloat(discountAmount);

                  if (amount > 0) {
                    addDiscountMutation.mutate({
                      amount: amount,
                      reason: discountReason.trim() || "Manual discount",
                      discountType: "manual",
                    });
                  } else {
                    toast({
                      title: "Error",
                      description: "Please enter a valid discount amount.",
                      variant: "destructive",
                    });
                  }
                }}
                disabled={
                  addDiscountMutation.isPending ||
                  !discountAmount ||
                  parseFloat(discountAmount) <= 0
