client/src/pages/login.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useAuth } from "@/hooks/use-auth";
import { Hospital, Loader2 } from "lucide-react";
import { useLocation } from "wouter";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { login, isAuthenticated } = useAuth();
  const [, setLocation] = useLocation();

  // Redirect if already authenticated
  if (isAuthenticated) {
    setLocation("/");
    return null;
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!username || !password) return;

    setIsLoading(true);
    try {
      await login(username, password);
      setLocation("/");
    } catch (error) {
      // Error handling is done in the login function
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-medical-blue/5 to-healthcare-green/5 p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center space-y-4">
          <div className="mx-auto w-16 h-16 bg-medical-blue rounded-2xl flex items-center justify-center">
            <Hospital className="w-8 h-8 text-white" />
          </div>
          <div>
            <CardTitle className="text-2xl font-bold text-text-dark">MedCare Pro</CardTitle>
            <CardDescription className="text-text-muted">
              Hospital Management System
            </CardDescription>
          </div>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="username" data-testid="label-username">Username</Label>
              <Input
                id="username"
                type="text"
                placeholder="Enter your username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                disabled={isLoading}
                data-testid="input-username"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="password" data-testid="label-password">Password</Label>
              <Input
                id="password"
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                disabled={isLoading}
                data-testid="input-password"
              />
            </div>
            <Button 
              type="submit" 
              className="w-full bg-medical-blue hover:bg-medical-blue/90"
              disabled={isLoading || !username || !password}
              data-testid="button-login"
            >
              {isLoading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Signing in...
                </>
              ) : (
                "Sign In"
              )}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/discharged-today.tsx.content:
import { useState, useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import TopBar from "@/components/layout/topbar";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { 
  UserX, 
  Calendar, 
  Clock,
  Search,
  Building2,
  Stethoscope,
  Phone,
  IndianRupee,
  TrendingUp,
  User
} from "lucide-react";
import type { Admission, Patient, Doctor } from "@shared/schema";

interface AdmissionWithDetails extends Admission {
  patient: Patient;
  doctor: Doctor | null;
}

export default function DischargedTodayPage() {
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch today's discharges with IST timezone
  const { data: todayDischarges = [], isLoading } = useQuery<AdmissionWithDetails[]>({
    queryKey: ["/api/inpatients/discharged-today"],
    staleTime: 0, // Always refetch for real-time data
    refetchOnMount: true,
    refetchOnWindowFocus: true,
  });

  // Filter discharges based on search
  const filteredDischarges = useMemo(() => {
    if (!searchQuery) return todayDischarges;

    return todayDischarges.filter(admission => {
      const searchLower = searchQuery.toLowerCase();
      return (
        admission.patient?.name.toLowerCase().includes(searchLower) ||
        admission.patient?.patientId.toLowerCase().includes(searchLower) ||
        admission.admissionId.toLowerCase().includes(searchLower) ||
        admission.currentWardType?.toLowerCase().includes(searchLower) ||
        admission.doctor?.name.toLowerCase().includes(searchLower) ||
        admission.diagnosis?.toLowerCase().includes(searchLower)
      );
    });
  }, [todayDischarges, searchQuery]);

  const formatTime = (dateTimeString: string) => {
    if (!dateTimeString) return 'N/A';
    const date = new Date(dateTimeString);
    return date.toLocaleTimeString('en-IN', { 
      hour12: true, 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const calculateStayDuration = (admissionDate: string, dischargeDate: string) => {
    if (!dischargeDate) return 0;
    const admission = new Date(admissionDate);
    const discharge = new Date(dischargeDate);
    const diffTime = Math.abs(discharge.getTime() - admission.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  const getTotalRevenue = () => {
    return todayDischarges.reduce((sum, admission) => sum + (admission.totalCost || 0), 0);
  };

  const getAverageStay = () => {
    if (todayDischarges.length === 0) return 0;
    const totalDays = todayDischarges.reduce((sum, admission) => 
      sum + calculateStayDuration(admission.admissionDate, admission.dischargeDate || ''), 0);
    return Math.round(totalDays / todayDischarges.length);
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <TopBar title="Patients Discharged Today" />
        <div className="p-6">
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <TopBar title="Patients Discharged Today" />

      <div className="p-6">
        {/* Search and Filters */}
        <Card className="mb-6">
          <CardContent className="p-4">
            <div className="flex gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search by patient name, ID, admission ID, ward type, doctor, or diagnosis..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Discharges Table */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <UserX className="h-5 w-5" />
              Today's Discharges ({filteredDischarges.length})
            </CardTitle>
            <CardDescription>
              Patients discharged today (IST timezone) - {new Date().toLocaleDateString('en-IN')}
            </CardDescription>
          </CardHeader>
          <CardContent>
            {filteredDischarges.length > 0 ? (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Patient Details</TableHead>
                    <TableHead>Stay Details</TableHead>
                    <TableHead>Ward/Room</TableHead>
                    <TableHead>Doctor</TableHead>
                    <TableHead>Diagnosis</TableHead>
                    <TableHead>Total Cost</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredDischarges.map((admission) => (
                    <TableRow key={admission.id}>
                      <TableCell>
                        <div>
                          <div className="font-medium">{admission.patient?.name}</div>
                          <div className="text-sm text-gray-500">
                            ID: {admission.patient?.patientId}
                          </div>
                          <div className="text-sm text-gray-500 flex items-center gap-1">
                            <Phone className="h-3 w-3" />
                            {admission.patient?.phone}
                          </div>
                          <div className="text-sm text-gray-500">
                            Age: {admission.patient?.age}  {admission.patient?.gender}
                          </div>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div>
                          <div className="font-medium text-sm">{admission.admissionId}</div>
                          <div className="text-sm text-gray-500 flex items-center gap-1">
                            <Calendar className="h-3 w-3" />
                            {new Date(admission.admissionDate).toLocaleDateString('en-IN')} - {admission.dischargeDate ? new Date(admission.dischargeDate).toLocaleDateString('en-IN') : 'N/A'}
                          </div>
                          <div className="text-sm text-gray-500 flex items-center gap-1">
                            <Clock className="h-3 w-3" />
                            Discharged: {formatTime(admission.updatedAt)}
                          </div>
                          <Badge variant="outline" className="mt-1">
                            {calculateStayDuration(admission.admissionDate, admission.dischargeDate || '')} days
                          </Badge>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div>
                          <div className="font-medium">{admission.currentWardType || "Not specified"}</div>
                          <div className="text-sm text-gray-500">
                            Room: {admission.currentRoomNumber || "N/A"}
                          </div>
                          <div className="text-sm text-gray-500">
                            Daily: {admission.dailyCost.toLocaleString()}
                          </div>
                        </div>
                      </TableCell>
                      <TableCell>
                        {admission.doctor ? (
                          <div>
                            <div className="font-medium text-sm flex items-center gap-1">
                              <Stethoscope className="h-3 w-3" />
                              {admission.doctor.name}
                            </div>
                            <div className="text-xs text-gray-500">
                              {admission.doctor.specialization}
                            </div>
                          </div>
                        ) : (
                          <span className="text-gray-400">No doctor assigned</span>
                        )}
                      </TableCell>
                      <TableCell>
                        <div className="max-w-xs">
                          {admission.diagnosis ? (
                            <p className="text-sm">{admission.diagnosis}</p>
                          ) : (
                            <span className="text-gray-400">No diagnosis recorded</span>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="font-medium text-green-600">
                          {(admission.totalCost || 0).toLocaleString()}
                        </div>
                        <div className="text-xs text-gray-500">
                          Deposit: {(admission.initialDeposit || 0).toLocaleString()}
                        </div>
                        <Badge 
                          variant={admission.status === 'discharged' ? 'default' : 'secondary'}
                          className="mt-1"
                        >
                          Discharged
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <Link href={`/patients/${admission.patientId}`}>
                          <Button variant="outline" size="sm">
                            View Patient
                          </Button>
                        </Link>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            ) : (
              <div className="text-center py-8">
                <UserX className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
                <p className="text-muted-foreground">
                  {searchQuery ? "No discharges match your search criteria." : "No patients were discharged today."}
                </p>
                <Link href="/admissions">
                  <Button className="mt-4">
                    View All Admissions
                  </Button>
                </Link>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/hooks/use-timezone.ts.content:
import { useQuery } from "@tanstack/react-query";

/**
 * Hook to get timezone-aware formatting functions
 * These functions will automatically use the configured hospital timezone
 */
export function useTimezone() {
  const { data: systemSettings } = useQuery({
    queryKey: ["/api/settings/system"],
  });

  const timezone = systemSettings?.timezone || "UTC";

  /**
   * Format a UTC timestamp for human-readable display in the configured timezone
   * @param utcDateString - ISO 8601 UTC timestamp string from the server
   * @returns Formatted date and time string (e.g., "Oct 2, 2025, 8:30 PM")
   */
  const formatDateTime = (utcDateString: string | null | undefined): string => {
    if (!utcDateString) return "N/A";

    try {
      // Ensure the date string is treated as UTC
      let utcDate: Date;
      if (utcDateString.endsWith('Z') || utcDateString.includes('+') || utcDateString.includes('T')) {
        // Already in ISO format with timezone info
        utcDate = new Date(utcDateString);
      } else {
        // Assume UTC if no timezone info (append Z)
        utcDate = new Date(utcDateString + 'Z');
      }

      if (isNaN(utcDate.getTime())) {
        return "N/A";
      }

      return new Intl.DateTimeFormat("en-US", {
        timeZone: timezone,
        month: "short",
        day: "numeric",
        year: "numeric",
        hour: "numeric",
        minute: "2-digit",
        hour12: true,
      }).format(utcDate);
    } catch (error) {
      console.error("Error formatting timestamp with timezone:", error);
      return "N/A";
    }
  };

  /**
   * Format a UTC timestamp for date-only display in the configured timezone
   * @param utcDateString - ISO 8601 UTC timestamp string from the server
   * @returns Formatted date string (e.g., "Oct 2, 2025")
   */
  const formatDate = (utcDateString: string | null | undefined): string => {
    if (!utcDateString) return "N/A";

    try {
      // Ensure the date string is treated as UTC
      let utcDate: Date;
      if (utcDateString.endsWith('Z') || utcDateString.includes('+') || utcDateString.includes('T')) {
        utcDate = new Date(utcDateString);
      } else {
        utcDate = new Date(utcDateString + 'Z');
      }

      if (isNaN(utcDate.getTime())) {
        return "N/A";
      }

      return new Intl.DateTimeFormat("en-US", {
        timeZone: timezone,
        month: "short",
        day: "numeric",
        year: "numeric",
      }).format(utcDate);
    } catch (error) {
      console.error("Error formatting date with timezone:", error);
      return "N/A";
    }
  };

  /**
   * Format a UTC timestamp for time-only display in the configured timezone
   * @param utcDateString - ISO 8601 UTC timestamp string from the server
   * @returns Formatted time string (e.g., "8:30 PM")
   */
  const formatTime = (utcDateString: string | null | undefined): string => {
    if (!utcDateString) return "N/A";

    try {
      // Ensure the date string is treated as UTC
      let utcDate: Date;
      if (utcDateString.endsWith('Z') || utcDateString.includes('+') || utcDateString.includes('T')) {
        utcDate = new Date(utcDateString);
      } else {
        utcDate = new Date(utcDateString + 'Z');
      }

      if (isNaN(utcDate.getTime())) {
        return "N/A";
      }

      return new Intl.DateTimeFormat("en-US", {
        timeZone: timezone,
        hour: "numeric",
        minute: "2-digit",
        hour12: true,
      }).format(utcDate);
    } catch (error) {
      console.error("Error formatting time with timezone:", error);
      return "N/A";
    }
  };

  return {
    timezone,
    formatDateTime,
    formatDate,
    formatTime,
  };
}
